const WebinarLiveView = ({ webinar, onClose }) => {
  const [activePanel, setActivePanel] = useState('chat'); // chat, qa, polls
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState('');
  const [qaQuestions, setQaQuestions] = useState([]);
  const [qaInput, setQaInput] = useState('');
  const [polls, setPolls] = useState([]);
  const [registrations, setRegistrations] = useState([]);

  useEffect(() => {
    fetchChatMessages();
    fetchQAQuestions();
    fetchPolls();
    fetchRegistrations();

    // Poll for new messages every 3 seconds
    const chatInterval = setInterval(fetchChatMessages, 3000);
    const qaInterval = setInterval(fetchQAQuestions, 5000);

    return () => {
      clearInterval(chatInterval);
      clearInterval(qaInterval);
    };
  }, [webinar.id]);

  const fetchChatMessages = async () => {
    try {
      const response = await api.get(`/api/webinars/${webinar.id}/chat`);
      setChatMessages(response.data.messages || []);
    } catch (error) {
      console.error('Error fetching chat:', error);
    }
  };

  const fetchQAQuestions = async () => {
    try {
      const response = await api.get(`/api/webinars/${webinar.id}/qa`);
      setQaQuestions(response.data.questions || []);
    } catch (error) {
      console.error('Error fetching Q&A:', error);
    }
  };

  const fetchPolls = async () => {
    try {
      const response = await api.get(`/api/webinars/${webinar.id}/polls`);
      setPolls(response.data.polls || []);
    } catch (error) {
      console.error('Error fetching polls:', error);
    }
  };

  const fetchRegistrations = async () => {
    try {
      const response = await api.get(`/api/webinars/${webinar.id}/registrations`);
      setRegistrations(response.data.registrations || []);
    } catch (error) {
      console.error('Error fetching registrations:', error);
    }
  };

  const sendChatMessage = async () => {
    if (!chatInput.trim()) return;

    try {
      await api.post(`/api/webinars/${webinar.id}/chat`, {
        message: chatInput,
        sender_name: 'Host',
        webinar_id: webinar.id
      });
      setChatInput('');
      fetchChatMessages();
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  const handleStartWebinar = async () => {
    try {
      await api.post(`/api/webinars/${webinar.id}/start`);
      alert('Webinar started!');
    } catch (error) {
      console.error('Error starting webinar:', error);
    }
  };

  const handleEndWebinar = async () => {
    if (!window.confirm('Are you sure you want to end the webinar?')) return;

    try {
      await api.post(`/api/webinars/${webinar.id}/end`);
      alert('Webinar ended');
      onClose();
    } catch (error) {
      console.error('Error ending webinar:', error);
    }
  };

  return (
    <div className="h-screen flex flex-col bg-gray-900">
      {/* Header */}
      <div className="bg-gray-800 border-b border-gray-700 px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-white transition"
            >
              <X size={24} />
            </button>
            <div>
              <h2 className="text-xl font-bold text-white">{webinar.title}</h2>
              <p className="text-sm text-gray-400">
                {registrations.length} registered  {webinar.status === 'live' ? 'LIVE' : 'Not Started'}
              </p>
            </div>
          </div>

          <div className="flex items-center gap-3">
            {webinar.status !== 'live' && (
              <button
                onClick={handleStartWebinar}
                className="flex items-center gap-2 bg-green-600 text-white px-6 py-2 rounded-lg hover:bg-green-700 transition font-medium"
              >
                <Play size={20} />
                Start Webinar
              </button>
            )}
            {webinar.status === 'live' && (
              <button
                onClick={handleEndWebinar}
                className="flex items-center gap-2 bg-red-600 text-white px-6 py-2 rounded-lg hover:bg-red-700 transition font-medium"
              >
                <Square size={20} />
                End Webinar
              </button>
            )}
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex overflow-hidden">
        {/* Video Area */}
        <div className="flex-1 bg-black flex items-center justify-center">
          <div className="text-center text-white">
            <Video size={64} className="mx-auto mb-4 opacity-50" />
            <h3 className="text-2xl font-bold mb-2">Live Video Stream</h3>
            <p className="text-gray-400">Mock video player - integrate with your streaming service</p>
            {webinar.status === 'live' && (
              <div className="mt-4 flex items-center justify-center gap-2">
                <div className="w-3 h-3 bg-red-500 rounded-full animate-pulse"></div>
                <span className="text-red-500 font-medium">LIVE</span>
              </div>
            )}
          </div>
        </div>

        {/* Side Panel */}
        <div className="w-96 bg-gray-800 border-l border-gray-700 flex flex-col">
          {/* Panel Tabs */}
          <div className="flex border-b border-gray-700">
            <button
              onClick={() => setActivePanel('chat')}
              className={`flex-1 px-4 py-3 text-sm font-medium transition ${
                activePanel === 'chat'
                  ? 'bg-gray-700 text-white'
                  : 'text-gray-400 hover:text-white'
              }`}
            >
              <MessageSquare size={16} className="inline mr-2" />
              Chat
            </button>
            <button
              onClick={() => setActivePanel('qa')}
              className={`flex-1 px-4 py-3 text-sm font-medium transition ${
                activePanel === 'qa'
                  ? 'bg-gray-700 text-white'
                  : 'text-gray-400 hover:text-white'
              }`}
            >
              <HelpCircle size={16} className="inline mr-2" />
              Q&A
            </button>
            <button
              onClick={() => setActivePanel('polls')}
              className={`flex-1 px-4 py-3 text-sm font-medium transition ${
                activePanel === 'polls'
                  ? 'bg-gray-700 text-white'
                  : 'text-gray-400 hover:text-white'
              }`}
            >
              <BarChart3 size={16} className="inline mr-2" />
              Polls
            </button>
          </div>

          {/* Panel Content */}
          <div className="flex-1 overflow-y-auto p-4">
            {activePanel === 'chat' && (
              <ChatPanel
                messages={chatMessages}
                input={chatInput}
                setInput={setChatInput}
                onSend={sendChatMessage}
              />
            )}
            {activePanel === 'qa' && (
              <QAPanel questions={qaQuestions} webinarId={webinar.id} onRefresh={fetchQAQuestions} />
            )}
            {activePanel === 'polls' && (
              <PollsPanel polls={polls} webinarId={webinar.id} onRefresh={fetchPolls} />
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

const ChatPanel = ({ messages, input, setInput, onSend }) => {
  return (
    <div className="h-full flex flex-col">
      <div className="flex-1 overflow-y-auto space-y-3 mb-4">
        {messages.length === 0 ? (
          <div className="text-center text-gray-500 py-8">
            <MessageSquare size={32} className="mx-auto mb-2 opacity-50" />
            <p className="text-sm">No messages yet</p>
          </div>
        ) : (
          messages.map((msg) => (
            <div key={msg.id} className="bg-gray-700 rounded-lg p-3">
              <div className="flex items-center gap-2 mb-1">
                <span className="text-sm font-medium text-white">{msg.sender_name}</span>
                {msg.is_host && (
                  <span className="text-xs bg-blue-500 text-white px-2 py-0.5 rounded">Host</span>
                )}
              </div>
              <p className="text-sm text-gray-300">{msg.message}</p>
              <p className="text-xs text-gray-500 mt-1">
                {new Date(msg.created_at).toLocaleTimeString()}
              </p>
            </div>
          ))
        )}
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && onSend()}
          placeholder="Type a message..."
          className="flex-1 px-4 py-2 bg-gray-700 text-white border border-gray-600 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        />
        <button
          onClick={onSend}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition"
        >
          <Send size={20} />
        </button>
      </div>
    </div>
  );
};

const QAPanel = ({ questions, webinarId, onRefresh }) => {
  const [answerInput, setAnswerInput] = useState('');
  const [selectedQuestion, setSelectedQuestion] = useState(null);

  const handleAnswer = async () => {
    if (!answerInput.trim() || !selectedQuestion) return;

    try {
      await api.put(`/api/webinars/${webinarId}/qa/${selectedQuestion}/answer?answer_text=${encodeURIComponent(answerInput)}`);
      setAnswerInput('');
      setSelectedQuestion(null);
      onRefresh();
    } catch (error) {
      console.error('Error answering question:', error);
    }
  };

  return (
    <div className="space-y-3">
      {questions.length === 0 ? (
        <div className="text-center text-gray-500 py-8">
          <HelpCircle size={32} className="mx-auto mb-2 opacity-50" />
          <p className="text-sm">No questions yet</p>
        </div>
      ) : (
        questions.map((q) => (
          <div key={q.id} className="bg-gray-700 rounded-lg p-4">
            <div className="flex items-start justify-between mb-2">
              <div className="flex-1">
                <p className="text-sm font-medium text-white mb-1">{q.question}</p>
                <p className="text-xs text-gray-400">Asked by {q.asker_name}</p>
              </div>
              <button
                className="text-gray-400 hover:text-blue-400 transition"
              >
                <ThumbsUp size={16} />
                <span className="text-xs ml-1">{q.upvotes}</span>
              </button>
            </div>

            {q.is_answered ? (
              <div className="mt-3 pt-3 border-t border-gray-600">
                <p className="text-xs text-gray-400 mb-1">Answer:</p>
                <p className="text-sm text-gray-300">{q.answer}</p>
              </div>
            ) : (
              <div className="mt-3 pt-3 border-t border-gray-600">
                {selectedQuestion === q.id ? (
                  <div className="space-y-2">
                    <textarea
                      value={answerInput}
                      onChange={(e) => setAnswerInput(e.target.value)}
                      placeholder="Type your answer..."
                      rows={3}
                      className="w-full px-3 py-2 bg-gray-600 text-white border border-gray-500 rounded-lg text-sm focus:ring-2 focus:ring-blue-500"
                    />
                    <div className="flex gap-2">
                      <button
                        onClick={handleAnswer}
                        className="flex-1 bg-blue-600 text-white px-3 py-1.5 rounded text-sm hover:bg-blue-700"
                      >
                        Submit Answer
                      </button>
                      <button
                        onClick={() => {
                          setSelectedQuestion(null);
                          setAnswerInput('');
                        }}
                        className="bg-gray-600 text-white px-3 py-1.5 rounded text-sm hover:bg-gray-500"
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  <button
                    onClick={() => setSelectedQuestion(q.id)}
                    className="text-sm text-blue-400 hover:text-blue-300 transition"
                  >
                    Answer this question
                  </button>
                )}
              </div>
            )}
          </div>
        ))
      )}
    </div>
  );
};

const PollsPanel = ({ polls, webinarId, onRefresh }) => {
  return (
    <div className="space-y-4">
      {polls.length === 0 ? (
        <div className="text-center text-gray-500 py-8">
          <BarChart3 size={32} className="mx-auto mb-2 opacity-50" />
          <p className="text-sm">No polls yet</p>
        </div>
      ) : (
        polls.map((poll) => (
          <div key={poll.id} className="bg-gray-700 rounded-lg p-4">
            <h4 className="text-white font-medium mb-3">{poll.question}</h4>
            <div className="space-y-2">
              {poll.options.map((option, idx) => {
                const votes = poll.votes[idx] || 0;
                const percentage = poll.total_votes > 0 ? (votes / poll.total_votes) * 100 : 0;

                return (
                  <div key={idx} className="bg-gray-600 rounded-lg p-3">
                    <div className="flex items-center justify-between mb-1">
                      <span className="text-sm text-gray-300">{option}</span>
                      <span className="text-xs text-gray-400">{votes} votes</span>
                    </div>
                    <div className="w-full bg-gray-500 rounded-full h-2">
                      <div
                        className="bg-blue-500 h-2 rounded-full transition-all"
                        style={{ width: `${percentage}%` }}
                      ></div>
                    </div>
                  </div>
                );
              })}
            </div>
            <p className="text-xs text-gray-400 mt-3">
              Total votes: {poll.total_votes}
            </p>
          </div>
        ))
      )}
    </div>
  );
};

const RecordingsPanel = ({ webinars, recordings, onRefresh, onAddRecording, loading }) => {
  const [selectedWebinar, setSelectedWebinar] = useState(null);
  const [showUploadModal, setShowUploadModal] = useState(false);

  const handleAddRecording = (webinarId) => {
    const webinar = webinars.find(w => w.id === webinarId);
    setSelectedWebinar(webinar);
    setShowUploadModal(true);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <>
      <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-xl font-bold text-gray-900">Webinar Recordings</h3>
          <button
            onClick={() => setShowUploadModal(true)}
            className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition text-sm"
          >
            <Upload size={16} />
            Add Recording
          </button>
        </div>

        {recordings.length === 0 ? (
          <div className="text-center py-12">
            <Video size={48} className="mx-auto text-gray-400 mb-4" />
            <h4 className="text-lg font-semibold text-gray-700 mb-2">No recordings yet</h4>
            <p className="text-gray-600 mb-4">Upload recordings after your webinars end</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {recordings.map((recording) => (
              <RecordingCard
                key={recording.id}
                recording={recording}
                onRefresh={onRefresh}
              />
            ))}
          </div>
        )}
      </div>

      {showUploadModal && (
        <UploadRecordingModal
          webinars={webinars}
          webinar={selectedWebinar}
          onClose={() => {
            setShowUploadModal(false);
            setSelectedWebinar(null);
          }}
          onSuccess={() => {
            setShowUploadModal(false);
            setSelectedWebinar(null);
            onRefresh();
          }}
        />
      )}
    </>
  );
};

const RecordingCard = ({ recording, onRefresh }) => {
  const [showPlayer, setShowPlayer] = useState(false);

  const handleDelete = async () => {
    if (!window.confirm('Are you sure you want to delete this recording?')) return;

    try {
      await api.delete(`/api/webinars/${recording.webinar_id}/recordings/${recording.id}`);
      onRefresh();
    } catch (error) {
      console.error('Error deleting recording:', error);
      alert('Failed to delete recording');
    }
  };

  const handleTogglePublic = async () => {
    try {
      await api.put(`/api/webinars/${recording.webinar_id}/recordings/${recording.id}`, {
        is_public: !recording.is_public
      });
      onRefresh();
    } catch (error) {
      console.error('Error updating recording:', error);
    }
  };

  if (showPlayer) {
    return <RecordingPlayer recording={recording} onClose={() => setShowPlayer(false)} />;
  }

  return (
    <div className="bg-white border border-gray-200 rounded-lg overflow-hidden hover:shadow-md transition">
      <div className="h-40 bg-gradient-to-br from-purple-500 to-pink-500 relative">
        {recording.thumbnail_url ? (
          <img src={recording.thumbnail_url} alt={recording.title} className="w-full h-full object-cover" />
        ) : (
          <div className="flex items-center justify-center h-full">
            <Video className="text-white opacity-50" size={48} />
          </div>
        )}
        <button
          onClick={() => setShowPlayer(true)}
          className="absolute inset-0 flex items-center justify-center bg-black bg-opacity-30 hover:bg-opacity-50 transition"
        >
          <div className="w-16 h-16 bg-white rounded-full flex items-center justify-center">
            <Play className="text-purple-600 ml-1" size={28} />
          </div>
        </button>
      </div>

      <div className="p-4">
        <h4 className="font-semibold text-gray-900 mb-1 line-clamp-1">{recording.title}</h4>
        <p className="text-sm text-gray-600 mb-3 line-clamp-1">{recording.webinar_title}</p>

        <div className="flex items-center gap-2 text-sm text-gray-600 mb-4">
          <Clock size={14} />
          <span>{recording.duration_minutes || 0} minutes</span>
          <span className="mx-2"></span>
          <Eye size={14} />
          <span>{recording.view_count || 0} views</span>
        </div>

        <div className="flex gap-2">
          <button
            onClick={handleTogglePublic}
            className={`flex-1 px-3 py-2 rounded text-sm font-medium transition ${
              recording.is_public
                ? 'bg-green-100 text-green-700 hover:bg-green-200'
                : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
            }`}
          >
            {recording.is_public ? 'Public' : 'Private'}
          </button>
          <button
            onClick={handleDelete}
            className="bg-red-100 text-red-600 px-3 py-2 rounded hover:bg-red-200 transition"
          >
            <Trash2 size={16} />
          </button>
        </div>
      </div>
    </div>
  );
};

const UploadRecordingModal = ({ webinars, webinar, onClose, onSuccess }) => {
  const [formData, setFormData] = useState({
    webinar_id: webinar?.id || '',
    title: '',
    recording_url: '',
    duration_minutes: '',
    thumbnail_url: '',
    is_public: false
  });
  const [saving, setSaving] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);

    try {
      const payload = {
        ...formData,
        duration_minutes: formData.duration_minutes ? parseInt(formData.duration_minutes) : null
      };

      await api.post(`/api/webinars/${formData.webinar_id}/recordings`, payload);
      onSuccess();
    } catch (error) {
      console.error('Error saving recording:', error);
      alert('Failed to save recording');
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6 border-b border-gray-200 flex items-center justify-between sticky top-0 bg-white">
          <h2 className="text-2xl font-bold text-gray-900">Add Recording</h2>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
            <X size={24} />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Select Webinar *
            </label>
            <select
              value={formData.webinar_id}
              onChange={(e) => setFormData({ ...formData, webinar_id: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              required
            >
              <option value="">Choose a webinar...</option>
              {webinars.filter(w => w.status === 'ended').map(w => (
                <option key={w.id} value={w.id}>{w.title}</option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Recording Title *
            </label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="e.g., Master Digital Marketing - Recording"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Recording URL * (YouTube, Vimeo, etc.)
            </label>
            <input
              type="url"
              value={formData.recording_url}
              onChange={(e) => setFormData({ ...formData, recording_url: e.target.value })}
              className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              placeholder="https://youtube.com/watch?v=..."
              required
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Duration (minutes)
              </label>
              <input
                type="number"
                value={formData.duration_minutes}
                onChange={(e) => setFormData({ ...formData, duration_minutes: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="60"
                min="1"
              />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Thumbnail URL
              </label>
              <input
                type="url"
                value={formData.thumbnail_url}
                onChange={(e) => setFormData({ ...formData, thumbnail_url: e.target.value })}
                className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                placeholder="https://..."
              />
            </div>
          </div>

          <label className="flex items-center gap-3">
            <input
              type="checkbox"
              checked={formData.is_public}
              onChange={(e) => setFormData({ ...formData, is_public: e.target.checked })}
              className="w-5 h-5 text-blue-600 rounded focus:ring-2 focus:ring-blue-500"
            />
            <span className="text-sm text-gray-700">Make recording publicly accessible</span>
          </label>

          <div className="flex gap-3 pt-4">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition font-medium"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={saving}
              className="flex-1 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition font-medium disabled:opacity-50"
            >
              {saving ? 'Saving...' : 'Add Recording'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

